"use strict";(self.webpackChunkgenai_llm_documentation_website=self.webpackChunkgenai_llm_documentation_website||[]).push([[1119],{24283(e){e.exports=JSON.parse('{"tag":{"label":"governance","permalink":"/gen-ai-llm-docs/docs/tags/governance","allTagsPath":"/gen-ai-llm-docs/docs/tags","count":10,"items":[{"id":"04-responsible-ai/01-accountability-and-delegation","title":"Accountability and Delegation Model","description":"Clarify roles, responsibilities, and decision-making authority when working with AI. This model ensures that humans remain accountable for outcomes, even when delegating tasks to AI tools.","permalink":"/gen-ai-llm-docs/docs/04-responsible-ai/01-accountability-and-delegation"},{"id":"04-responsible-ai/accountability-model","title":"Accountability Model","description":"AI is a tool, not a colleague. It cannot be fired, sued, or held responsible. Therefore, a human must own every output.","permalink":"/gen-ai-llm-docs/docs/04-responsible-ai/accountability-model"},{"id":"04-responsible-ai/data-boundaries","title":"Data Boundaries","description":"Explicitly define what data types can (and cannot) be exposed to AI tools. This guardrail is critical for protecting sensitive information, ensuring compliance with data privacy regulations, and preventing data leakage in AI-assisted workflows.","permalink":"/gen-ai-llm-docs/docs/04-responsible-ai/data-boundaries"},{"id":"01-handbook-core-method/accountability-and-delegation","title":"GenAI & LLM Documentation Accountability and Delegation Model","description":"Clarify roles, responsibilities, and decision-making authority when working with AI. This model ensures that humans remain accountable for outcomes, even when delegating tasks to AI tools.","permalink":"/gen-ai-llm-docs/docs/01-handbook-core-method/accountability-and-delegation"},{"id":"00-handbook-introduction/scope-and-applicability","title":"GenAI & LLM Handbook: Scope and Applicability","description":"Draw a clear boundary for when the GenAI & LLM Handbook applies, what it governs, and when it may be skipped. Define \u201cprofessional environment\u201d in impact terms, not job titles.","permalink":"/gen-ai-llm-docs/docs/00-handbook-introduction/scope-and-applicability"},{"id":"foundations/02-llm-deep-dive/fundamentals/10-genai-product-lifecycle","title":"GenAI Product Lifecycle","description":"Integrate Generative AI capabilities into your product development lifecycle, from ideation to deployment and maintenance. This guide ensures that AI features are developed responsibly, align with user needs, and are governed effectively, leveraging GenAI & LLM Documentation principles throughout.","permalink":"/gen-ai-llm-docs/docs/foundations/02-llm-deep-dive/fundamentals/10-genai-product-lifecycle"},{"id":"01-handbook-core-method/01-overview","title":"GenAI Project Lifecycle Overview","description":"Integrate Generative AI capabilities into your project development lifecycle, from ideation to deployment and maintenance. This guide ensures that AI features are developed responsibly, align with user needs, and are governed effectively.","permalink":"/gen-ai-llm-docs/docs/01-handbook-core-method/01-overview"},{"id":"04-responsible-ai/guardrails-index","title":"Guardrails and Governance Index","description":"Guardrails and Governance define the protective measures and policies necessary to ensure AI is used safely, ethically, and in compliance with organizational and regulatory standards. These are critical for managing the risks inherent in AI-assisted development.","permalink":"/gen-ai-llm-docs/docs/04-responsible-ai/guardrails-index"},{"id":"06-templates/working-agreements-template","title":"Template: Working Agreements","description":"Use this template to align your team on how AI tools will be used. These are not just rules; they are promises you make to each other to maintain quality and trust.","permalink":"/gen-ai-llm-docs/docs/06-templates/working-agreements-template"},{"id":"01-handbook-core-method/working-agreements-for-teams","title":"Working Agreements for Teams","description":"Establish clear, shared rules for how a team will collaborate with AI. These agreements foster a culture of accountability, trust, and effective AI integration, preventing friction and maximizing the benefits of GenAI & LLM Documentation.","permalink":"/gen-ai-llm-docs/docs/01-handbook-core-method/working-agreements-for-teams"}],"unlisted":false}}')}}]);