"use strict";(self.webpackChunkgenai_llm_documentation_website=self.webpackChunkgenai_llm_documentation_website||[]).push([[8722],{94914(e){e.exports=JSON.parse('{"tag":{"label":"template","permalink":"/gen-ai-llm-docs/docs/tags/template","allTagsPath":"/gen-ai-llm-docs/docs/tags","count":12,"items":[{"id":"06-templates/acceptance-criteria-template","title":"Acceptance Criteria Template","description":"Define objective, measurable conditions that must be met for an AI-generated output to be considered \\"done.\\" This transforms subjective \\"looks good\\" into evidence-based \\"is good,\\" preventing premature acceptance and ensuring quality.","permalink":"/gen-ai-llm-docs/docs/06-templates/acceptance-criteria-template"},{"id":"06-templates/constraint-spec-template","title":"Constraint Spec Template","description":"Encode all non-negotiable boundaries, technical requirements, and guardrails for an AI-assisted task. This prevents AI overreach, ensures compliance with architectural standards, and mitigates risks from hallucinations or unintended behavior.","permalink":"/gen-ai-llm-docs/docs/06-templates/constraint-spec-template"},{"id":"06-templates/generation-request-template","title":"Generation Request Template","description":"Formulate precise and structured prompts to AI, combining Intent and Constraints into an executable instruction. This maximizes the relevance and quality of AI-generated outputs, reducing iteration cycles.","permalink":"/gen-ai-llm-docs/docs/06-templates/generation-request-template"},{"id":"06-templates/intent-spec-template","title":"Intent Spec Template","description":"Clearly define the desired outcome and success criteria for an AI-assisted task. This prevents scope creep, focuses AI generation, and provides a clear benchmark for evaluating outputs.","permalink":"/gen-ai-llm-docs/docs/06-templates/intent-spec-template"},{"id":"06-templates/delegation-contract-template","title":"Template: Delegation Contract","description":"Use this to set the rules of engagement. Paste this into your chat or system prompt before starting the work.","permalink":"/gen-ai-llm-docs/docs/06-templates/delegation-contract-template"},{"id":"06-templates/discovery-brief-template","title":"Template: Discovery Brief","description":"Fill this out before writing any prompts or code. Keep it short. If you can\'t fill a section, pause and investigate.","permalink":"/gen-ai-llm-docs/docs/06-templates/discovery-brief-template"},{"id":"06-templates/iteration-log-template","title":"Template: Iteration Log","description":"Use this log when the first attempt fails. Do not just \\"regenerate.\\" Document what you changed in the specs to fix the error.","permalink":"/gen-ai-llm-docs/docs/06-templates/iteration-log-template"},{"id":"06-templates/quality-rubric-template","title":"Template: Quality Rubric","description":"Grade the artifact (code file, doc, etc.) on a scale of 1-3 for each dimension.","permalink":"/gen-ai-llm-docs/docs/06-templates/quality-rubric-template"},{"id":"06-templates/review-and-interrogation-template","title":"Template: Review & Interrogation","description":"Do not merge AI code without this review. Check the boxes based on evidence, not \\"it looks good.\\"","permalink":"/gen-ai-llm-docs/docs/06-templates/review-and-interrogation-template"},{"id":"06-templates/scenario-scorecard-template","title":"Template: Scenario Scorecard","description":"Use this scorecard to grade an end-to-end AI session. Be honest. If you skipped a step, mark it as skipped.","permalink":"/gen-ai-llm-docs/docs/06-templates/scenario-scorecard-template"},{"id":"06-templates/threat-model-lite-template","title":"Template: Threat Model Lite","description":"Fill this out for any feature that uses an LLM. If you answer \\"Yes\\" to any High Risk item, you must have a specific mitigation listed.","permalink":"/gen-ai-llm-docs/docs/06-templates/threat-model-lite-template"},{"id":"06-templates/working-agreements-template","title":"Template: Working Agreements","description":"Use this template to align your team on how AI tools will be used. These are not just rules; they are promises you make to each other to maintain quality and trust.","permalink":"/gen-ai-llm-docs/docs/06-templates/working-agreements-template"}],"unlisted":false}}')}}]);